        private FileInfo Profesores;
        private FileInfo Alumnos;

        private void crearFicheros()
        {
            Profesores = new FileInfo("Profesores.txt");
            Alumnos = new FileInfo("Alumnos.txt");
            if (!Profesores.Exists)
            {
                Profesores.Create();
            }
            if (!Alumnos.Exists)
            {
                Alumnos.Create();
            }
        }

        private void abrirProfesores()
        {
            try
            {
                System.Diagnostics.Process.Start("Profesores.txt");
            }
            catch (Exception)
            {
                MessageBox.Show("No se ha encontrado el archivo Profesores.txt");
            }
        }

        private void abrirAlumnos()
        {
            try
            {
                System.Diagnostics.Process.Start("Alumnos.txt");
            }
            catch (Exception)
            {
                MessageBox.Show("No se ha encontrado el archivo Alumnos.txt");
            }
        }
